 })
}

function enlargeVideo(id){
$(id).click(()=>{
    $(id).addClass('.videoHeight')
    console.log('done!')
})
}


$(document).ready(function() { 
    openVideoPage('.homeVideo')
    openVideoPage('#featureVideo')
